import React, { useEffect } from 'react';
import './index.css'; // Pastikan file CSS Tailwind di-import

const App = () => {
  useEffect(() => {
    const sections = document.querySelectorAll('section');
    sections.forEach(section => {
      section.style.opacity = '0';
      section.style.transform = 'translateY(20px)';
    });

    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.style.opacity = '1';
          entry.target.style.transform = 'translateY(0)';
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.1 });

    sections.forEach(section => observer.observe(section));

    document.querySelectorAll('.shadow-light').forEach(light => {
      light.style.left = `${Math.random() * (window.innerWidth - light.offsetWidth)}px`;
      light.style.top = `${Math.random() * (window.innerHeight - light.offsetHeight)}px`;
    });
  }, []);

  const renderShadowLights = (count) => (
    [...Array(count)].map((_, i) => (
      <div key={i} className="shadow-light absolute w-96 h-96 bg-[radial-gradient(circle,rgba(2,0,92,0.5),transparent_80%)] blur-[80px] pointer-events-none z-[-1] transition-transform duration-200 ease-in-out"></div>
    ))
