import React, { useEffect } from 'react';
import './index.css';

const App = () => {
  useEffect(() => {
    const sections = document.querySelectorAll('section');
    sections.forEach(section => {
      section.style.opacity = '0';
      section.style.transform = 'translateY(20px)';
    });

    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.style.opacity = '1';
          entry.target.style.transform = 'translateY(0)';
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.1 });

    sections.forEach(section => observer.observe(section));

    document.querySelectorAll('.shadow-light').forEach(light => {
      light.style.left = `${Math.random() * (window.innerWidth - light.offsetWidth)}px`;
      light.style.top = `${Math.random() * (window.innerHeight - light.offsetHeight)}px`;
    });
  }, []);

  const renderShadowLights = (count) => (
    [...Array(count)].map((_, i) => (
      <div key={i} className="shadow-light" style={{
        position: 'absolute',
        width: '400px',
        height: '400px',
        background: 'radial-gradient(circle, rgba(2, 0, 92, 0.5), transparent 80%)',
        filter: 'blur(80px)',
        pointerEvents: 'none',
        zIndex: -1,
        transition: 'transform 0.2s ease'
      }}></div>
    ))
  );

  return (
    <div style={{ backgroundColor: '#1a202c', color: 'white', fontFamily: 'sans-serif', overflowX: 'hidden', position: 'relative' }}>
      <div style={{
        position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
        background: `linear-gradient(to right, #4f4f4f2e 1px, transparent 2px),
                    linear-gradient(to bottom, #4f4f4f2e 1px, transparent 2px)`,
        backgroundSize: '24px 24px', zIndex: -1
      }}></div>
      <div style={{
        position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
        backgroundColor: 'rgba(0, 0, 0, 0.7)', zIndex: -1
      }}></div>

      <nav style={{
        position: 'fixed', top: 0, left: 0, width: '100%', padding: '1rem',
        background: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)',
        borderRadius: '12px', border: '1px solid rgba(255, 255, 255, 0.2)', zIndex: 50
      }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h1 style={{
            fontSize: '1.5rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent'
          }}>My Portfolio</h1>
          <div style={{ display: 'flex', gap: '1.5rem' }}>
            {['About', 'Skills', 'Projects', 'Contact'].map(section => (
              <a key={section} href={`#${section.toLowerCase()}`} style={{ color: 'white' }}>{section}</a>
            ))}
          </div>
        </div>
      </nav>

      <section style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', textAlign: 'center' }}>
        <div>
          <h1 style={{
            fontSize: '4.5rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '1rem'
          }}>
            Hi, I'm <span style={{ display: 'inline-block', overflow: 'hidden', whiteSpace: 'nowrap', animation: 'typing 3s steps(40, end)' }}>[Pedro]</span>
          </h1>
          <p style={{ fontSize: '1.25rem', color: '#a0aec0' }}>Full-Stack Developer | Tech Enthusiast | Problem Solver</p>
          <div style={{ marginTop: '2rem', display: 'flex', justifyContent: 'center' }}>
            <a href="#projects" style={{
              background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
              WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent',
              padding: '1rem 2rem', borderRadius: '9999px', boxShadow: '0 4px 20px rgba(0, 0, 139, 0.5)'
            }}>View My Work</a>
          </div>
        </div>
        {renderShadowLights(5)}
      </section>

      {/* About Section */}
      <section id="about" style={{ minHeight: '100vh', padding: '4rem 2rem' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
          <h2 style={{
            fontSize: '2.25rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '2rem'
          }}>About Me</h2>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr', gap: '2rem', alignItems: 'center' }}>
            <div style={{ animation: 'float 3s ease-in-out infinite' }}>
              <img src="/images/myself.jpg" alt="Profile" style={{
                width: '16rem', height: '16rem', borderRadius: '9999px',
                margin: '0 auto', boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)'
              }} />
            </div>
            <div style={{ fontSize: '1.125rem', color: '#a0aec0' }}>
              <p>Saya adalah seorang Full-Stack Developer dengan passion di bidang pengembangan web dan teknologi...</p>
              <p style={{ marginTop: '1rem' }}>Selain coding, saya juga suka berkolaborasi dengan tim...</p>
            </div>
          </div>
        </div>
        {renderShadowLights(8)}
      </section>

      {/* Skills Section */}
      <section id="skills" style={{ minHeight: '100vh', padding: '4rem 2rem' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
          <h2 style={{
            fontSize: '2.25rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '2rem', textAlign: 'center'
          }}>Skills</h2>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem' }}>
            {[
              { title: 'Python (Django, Flask)', percentage: '95%' },
              { title: 'JavaScript (React, Node.js)', percentage: '80%' }
            ].map(skill => (
              <div key={skill.title} style={{
                background: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)',
                borderRadius: '12px', border: '1px solid rgba(255, 255, 255, 0.2)',
                padding: '1.5rem', display: 'flex', flexDirection: 'column', alignItems: 'center'
              }}>
                <h3 style={{ fontSize: '1.25rem', fontWeight: 'bold', marginBottom: '1rem' }}>{skill.title}</h3>
                <div style={{ position: 'relative', width: '8rem', height: '8rem' }}>
                  <div style={{
                    width: '100%', height: '100%', borderRadius: '50%',
                    background: `conic-gradient(#00aaf9 ${skill.percentage === '95%' ? '95%' : '80%'}, #000000 ${skill.percentage === '95%' ? '95%' : '80%'}, #2d3748 0%)`,
                    WebkitMask: 'radial-gradient(farthest-side, transparent calc(100% - 8px), black calc(100% - 7px))',
                    animation: 'fill 1.5s ease-in-out forwards'
                  }}></div>
                  <span style={{
                    fontSize: '1.25rem', color: '#a0aec0',
                    position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)'
                  }}>{skill.percentage}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Experience Section */}
      <section id="experience" style={{ marginBottom: '2rem' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
          <h2 style={{
            fontSize: '2.25rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '2rem'
          }}>Experience</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>
            {[
              { title: 'Web Developer Intern', company: 'PT. Teknologi Maju Jaya', date: 'Jun 2019 - Des 2019', tasks: ['Mengembangkan aplikasi web internal menggunakan Django dan React.', 'Mengoptimalkan performa database.', 'Berpartisipasi dalam code review.'] },
              { title: 'Freelance Web Developer', company: 'Self-Employed', date: 'Jan 2020 - Sekarang', tasks: ['Membangun website untuk klien.', 'Mengintegrasikan sistem pembayaran.', 'Memberikan dukungan teknis.'] },
              { title: 'Security Officer (Part-Time)', company: 'PT. Keamanan Terpercaya', date: 'Mar 2018 - Des 2019', tasks: ['Menangani sistem keamanan.', 'Memastikan keamanan lingkungan kerja.'] }
            ].map(exp => (
              <div key={exp.title} style={{
                background: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)',
                borderRadius: '12px', border: '1px solid rgba(255, 255, 255, 0.2)',
                padding: '1.5rem'
              }}>
                <h3 style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.5rem' }}>{exp.title}</h3>
                <p style={{ color: '#a0aec0' }}>{exp.company}</p>
                <ul style={{ listStyleType: 'disc', marginLeft: '1.5rem', color: '#a0aec0', marginTop: '1rem' }}>
                  {exp.tasks.map(task => <li key={task}>{task}</li>)}
                </ul>
                <span style={{ fontSize: '0.875rem', color: '#a0aec0' }}>{exp.date}</span>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Education Section */}
      <section id="education" style={{ marginBottom: '2rem' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
          <h2 style={{
            fontSize: '2.25rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '2rem'
          }}>Education</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>
            {[
              { title: 'Sarjana Teknik Komputer', institution: 'Universitas XYZ', date: '2015 - 2019', details: ['IPK: 3.8/4.0', 'Fokus pada pengembangan web.', 'Aktif di organisasi mahasiswa.'] },
              { title: 'Proyek Skripsi', institution: 'Sistem Manajemen Inventaris Berbasis Web', date: '2018 - 2019', details: ['Membangun sistem inventaris.', 'Fitur manajemen stok dan laporan.'] }
            ].map(edu => (
              <div key={edu.title} style={{
                background: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)',
                borderRadius: '12px', border: '1px solid rgba(255, 255, 255, 0.2)',
                padding: '1.5rem'
              }}>
                <h3 style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.5rem' }}>{edu.title}</h3>
                <p style={{ color: '#a0aec0' }}>{edu.institution}</p>
                <ul style={{ listStyleType: 'disc', marginLeft: '1.5rem', color: '#a0aec0', marginTop: '1rem' }}>
                  {edu.details.map(detail => <li key={detail}>{detail}</li>)}
                </ul>
                <span style={{ fontSize: '0.875rem', color: '#a0aec0' }}>{edu.date}</span>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" style={{ minHeight: '100vh', padding: '4rem 2rem' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
          <h2 style={{
            fontSize: '2.25rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '2rem'
          }}>Projects</h2>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr', gap: '2rem' }}>
            {[
              { title: 'E-commerce Platform', description: 'Platform e-commerce dengan fitur lengkap.', image: '/images/project1.jpg' },
              { title: 'Blog Platform', description: 'Platform blog dengan fitur multi-user.', image: '/images/project2.jpg' },
              { title: 'Task Management App', description: 'Aplikasi manajemen tugas dengan fitur kolaborasi.', image: '/images/project3.jpg' }
            ].map(project => (
              <div key={project.title} style={{
                background: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)',
                borderRadius: '12px', border: '1px solid rgba(255, 255, 255, 0.2)',
                padding: '1.5rem'
              }}>
                <img src={project.image} alt={project.title} style={{ borderRadius: '12px', marginBottom: '1rem' }} />
                <h3 style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.5rem' }}>{project.title}</h3>
                <p style={{ color: '#a0aec0' }}>{project.description}</p>
                <a href="#" style={{ color: '#63b3ed', marginTop: '1rem', display: 'inline-block' }}>View Project ‚Üí</a>
              </div>
            ))}
          </div>
        </div>
        {renderShadowLights(2)}
      </section>

      {/* Contact Section */}
      <section id="contact" style={{ minHeight: '100vh', padding: '4rem 2rem', backgroundColor: '#2d3748' }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', textAlign: 'center' }}>
          <h2 style={{
            fontSize: '2.25rem', fontWeight: 'bold',
            background: 'linear-gradient(45deg, #6EE7B7, #3B82F6)',
            WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '2rem'
          }}>Let's Connect!</h2>
          <p style={{ fontSize: '1.125rem', color: '#a0aec0', marginBottom: '2rem' }}>Tertarik bekerja sama atau sekedar ngobrol? Jangan ragu untuk menghubungi saya!</p>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '1.5rem' }}>
            {['youremail@example.com', 'https://linkedin.com/in/yourprofile', 'https://github.com/yourprofile'].map((link, index) => (
              <a key={index} href={link} style={{ color: '#63b3ed' }}>{index === 0 ? 'üìß Email' : index === 1 ? 'üîó LinkedIn' : 'üê± GitHub'}</a>
            ))}
          </div>
        </div>
      </section>

      <footer style={{ padding: '2rem', textAlign: 'center', color: '#a0aec0' }}>
        <p>¬© 2024 [Your Name]. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
